import React from "react";
import {useEffect, useState} from "react"
import Web3 from "web3";
import Typography from '@material-ui/core/Typography'



export default function BalanceComponent(){
    const[balance, changeBalance] = useState(0);

    useEffect(() => {
        async function getAccounts(ethereum){
            return await ethereum.request({method: 'eth_accounts'})
        }
        async function getBalance(ethereum, address){
            const chuj = await ethereum.request({method: 'eth_getBalance', params: [address, "latest"]})
            console.log(web3.Convert.ToWei(chuj))
            return chuj
        }
        async function jd(){
        try{
            if(typeof window.ethereum !== "undefined"){
                console.log("connected")

            }else{
                alert("jd")
            }
            console.log("is ethereum")
            console.log(window.ethereum.isMetaMask)
            console.log(window.ethereum)
            const address =await getAccounts(window.ethereum)
            changeBalance(await getBalance(window.ethereum, address[0]))

        }catch(e){
            console.error(e + "error occurred")
        }
    }
    jd()
    }, [])

    return(
        <div>
            <Typography
            variant="h6"
            >Balance {balance} ETH</Typography>

        </div>
    )

}
