import React from "react";
import {useEffect, useState} from "react"
import Web3 from "web3";
import Typography from '@material-ui/core/Typography'



export default function BalanceComponent(){
    const[balance, changeBalance] = useState(0);

    useEffect(() => {
        async function getAccounts(ethereum){
            return await ethereum.request({method: 'eth_accounts'})
        }
        async function getBalance(ethereum, address){
            return await ethereum.request({method: 'eth_getBalance', params: [address, "latest"]})
        }

        try{
            if(typeof window.ethereum !== "undefined"){
                console.log("connected")

            }else{
                alert("jd")
            }
            console.log("is ethereum")
            console.log(window.ethereum.isMetaMask)

            const address = getAccounts(window.ethereum)
            console.log(getBalance(window.ethereum, address))

        }catch(e){
            console.error(e + "error occurred")
        }

    }, [])

    return(
        <div>
            <Typography
            variant="h6"
            >Balance {balance} ETH</Typography>

        </div>
    )

}
