    const[balance, changeBalance] = useState(0);

    useEffect(() => {
        try{
            const web3 = new Web3()
            const accounts = web3.eth.accounts;
            console.log(accounts)

        }catch(e){
            console.error(e + "error occurred")
        }

    }, [])

    return(
        <div>
            <Typography
            variant="h6"
            >Balance {balance} ETH</Typography>

        </div>
    )

}
