import logo from './logo.svg';
import './App.css';
import Web3 from "web3";
import React from "react"

import Payments from "./build/contracts/Payments.json"



class App extends React.Component {

  constructor(props) {
    super(props);
    this.state ={account: "", balance: "", address: "", amount: "", web3: null}
  }
  async componentDidMount() {
    try{
      let web3;
      if (window.ethereum){
        web3 = new Web3(window.ethereum);
      }else if(window.web3.currentProvider){
        web3 = new Web3(window.web3.currentProvider) 
      }else{
        alert("no provider available")
      }

      const accounts = await web3.eth.getAccounts();
      console.log(accounts[0])
      const balance = await web3.eth.getBalance(accounts[0]);
      this.setState({account: accounts[0], balance: balance, web3: web3})



    }catch(e){
      console.log(e)
    }

  }

  onChange = (event) =>{
    event.preventDefault();
    switch(event.target){
      case event.target === "email":
        this.setState({email: event.target.value})
        break;
      case event.target === "address":
        this.setState({address: event.target.value})
        break;
      default:
        break;
      }
  }

onSubmit = (event) =>{
  event.preventDefault()
  this.web3.eth.sendTransaction({
    from: this.state.account,
    to: this.state.address,
    value: this.state.amount,
  })


}

  render(){


    return (
      <div>
      <h1>Metmask address {this.state.account} Balance {this.state.balance}</h1>


      <form>
        <label>Address</label>
        <input name="address" type="text" onChange={this.onChange}></input>
        <label>Amount</label>
        <input name="amount" type="number" onChange={this.onChange}></input>
        <input type="submit" onSubmit={this.onSubmit}>Send!</input>

      </form>


      </div>

    )
  }
}
export default App;
