import React from 'react';
import {makeStyles} from '@material-ui/core'
import {useState} from "react"
import loadable from '@loadable/component'
import IconButton from "@material-ui/core/IconButton"
import VisibilityOutlinedIcon from '@material-ui/icons/VisibilityOutlined';
import VisibilityOffOutlinedIcon from '@material-ui/icons/VisibilityOffOutlined';
import Button from '@material-ui/core/Button';
import {useHistory} from "react-router-dom"
import CheckIfSessionCookieExists from "./CheckIfSessionCookieExists"

const TextField = loadable(()=> import("./FormFields/TextField"))

const styles = makeStyles((theme) =>({
    root:{
        display: "block",
        marginTop: theme.spacing(3),
        marginLeft: "40%", 
    },

}))

export default function FormComponent(props){
    const classes = styles()
    const isLoggedIn = CheckIfSessionCookieExists();
    let passwordFieldState = { true : "text", false: "password"}
    const [passwordVisibility, changePasswordVisibility] = useState(false);
    const [email, changeEmail] = useState();
    const [username, changeUsername] = useState();
    const [password, changePassword] = useState();
    const history = useHistory();

    const onChange = (e) =>{
        e.preventDefault();
        console.log(e.target.name)
        switch(e.target.name){
            case "email":
                changeEmail(e.target.value)
                break
            case "username":
                changeUsername(e.target.value)
                console.log(username)
                break
            case "password":
                changePassword(e.target.value)
                break
            default:
                break
        }
    }

    const onSubmit = (e) =>{
        switch(e.target.name){
            case "login":
                const passwordRegex = new RegExp('(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[^A-Za-z0-9])(?=.{8,})')
                if (email === ""){
                    console.log("empty email")
                }else if(password === "" || password.match(passwordRegex)){
                    console.log("empty or poor password")
                }else if(username === ""){
                    console.log("empty username")
                }
                break
            default:
                break
        }
    }
    if (!isLoggedIn){
        switch(props.version){
            case "login":
                return(
                <form onSubmit={onSubmit} name="login" method="post">
                    <TextField className={classes.form} type="text" id="username-login" label="username" name="username" onChange={onChange}></TextField>
                    <TextField className={classes.form} type="email" id="email-login" label="email" name="email" onChange={onChange}></TextField>
                    <TextField className={classes.form} type={passwordFieldState[passwordVisibility]} id="email-login" label="email" name="email" onChange={onChange}></TextField>
                    <IconButton onClick={()=>passwordVisibility===false ? changePasswordVisibility(true) : changePasswordVisibility(false)}>
                    {passwordVisibility===true ? <VisibilityOffOutlinedIcon/> : <VisibilityOutlinedIcon/>}
                    </IconButton>
                    <Button type="submit" variant="outlined" color="primary">Log In</Button>
                </form>
                )    
            case "register":
                break

            default:
                break
        }
    }else{
        switch(props.version){
            case "addListing":
                break
            default:
                break
        }
        return(
            <div>
                {history.push("/")}
            </div>
        )
    }
}
