import logo from './logo.svg';
import './App.css';
import Web3 from "web3";
import React from "react"

import Payments from "./build/contracts/Payments.json"



class App extends React.Component {

  constructor(props) {
    super(props);
    this.state ={account: "", balance: "", address: "", amount: "",}
  }
  async componentDidMount() {
    try{
      let web3;
      if (window.ethereum){
        web3 = new Web3(window.ethereum);
      }else if(window.web3.currentProvider){
        web3 = new Web3(window.web3.currentProvider) 
      }else{
        alert("no provider available")
      }

      const accounts = await web3.eth.getAccounts();
      console.log(accounts[0])
      const balance = await web3.eth.getBalance(accounts[0]);
      this.setState({account: accounts[0]})
      this.setState({balance: balance})


    }catch(e){
      console.log(e)
    }

  }

  onChange = (event) =>{
    switch(event.target){
      case event.target == "email":
        this.setState({email: event.target.value})
      case event.target == "address":
        this.setState({address: event.target.value})
    }



  }




  render(){


    return (
      <div>
      <h1>Metmask address {this.state.account} Balance {this.state.balance}</h1>


      <form>
        <label>Address</label>
        <input name="address" type="text"></input>
        <label>Amount</label>
        <input name="amount" type="number"></input>
        <input type="submit">Send!</input>

      </form>


      </div>

    )
  }
}
export default App;
